async (m, { conn, isOwner, command, isPremium }) => {
  const PREFIX = '^[.,!]';
  const commandsList = {};

  commands.forEach(cmd => {
    if (cmd.dontAddCommandList === false && cmd.command !== undefined) {
      try {
        const match = cmd.command.toString().match(/(\W*)([\w\s\d]*)/);
        const mmatch = match[2];
        const handler = PREFIX.match(/\[(\W*)\]/)?.[1]?.[0] || '.';
        if (!commandsList[cmd.type]) commandsList[cmd.type] = [];
        commandsList[cmd.type].push(handler + mmatch.trim());
      } catch (error) {
        console.error('Error processing command:', error);
      }
    }
  });

  // Gabungkan semua kategori menjadi satu pesan
  let bodyText = '';
  for (const category in commandsList) {
    bodyText += `*\`${category}\`*\n`;
    commandsList[category].forEach(cmd => {
      bodyText += `- â†’ ${cmd}\n`;
    });
    bodyText += '\n';  // Tambahkan pemisah antar kategori
  }

